      {todo:'todo 1'},
      {todo: 'todo 2'},
      {todo:'todo  3'}
   ])
   const handleChange=e=>setTodo({[e.target.name]:e.target.value})
   const handleClick=e=>console.log('click click')
 return (
    <>
    {
      todos.map((value,index) =>(<Todo todo={value.todo}/>))
    }
    </>
 )
}
